// jquery-isotope-example.js
function doSomething(){location.reload()}function init(){var e=$("#ports"),t={};e.isotope({itemSelector:".port"});$('.filter a[data-filter-value=""]').addClass("selected");$(".filter a").click(function(n){n.preventDefault();var r=$(this);if(r.hasClass("selected"))return;var i=r.parents(".option-set");i.find(".selected").removeClass("selected");r.addClass("selected");var s=i.attr("data-filter-group");t[s]=r.attr("data-filter-value");var o=[];for(var s in t)o.push(t[s]);var u=o.join("");e.isotope({filter:u})})}if(!/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)){var resizeTimer;$(window).resize(function(){clearTimeout(resizeTimer);resizeTimer=setTimeout(doSomething,100)})}$(document).ready(init);